import { useState } from "react";
import { Navigate, Route, Routes } from "react-router-dom";
import ListModel from "../../../Models/ListModel";
import AddList from "../../AddArea/addList/addList";
import AddTask from "../../AddArea/addTask/addTask";
import AddGift from "../../GiftsArea/AddGift/AddGift";
import GiftList from "../../GiftsArea/GiftList/GiftList";
import Home from "../../HomeArea/Home/Home";
import Tasks from "../../TasksArea/tasks/tasks";

import "./Routing.css";

function Routing({dr}): JSX.Element {

    // const [listId, setListId] = useState<ListModel>(); 

    const [drive, setDrive] = useState<string>(); // the lifted state

    const sendDataToParent = (id:any) => { // the callback. Use a better name
      console.log(id + "hi");
      setDrive(id);
    };

    return (
        <div>
			<Routes>
                <Route path="/home" element={<Home sendDataToParent={sendDataToParent}  />} />
                {/* <Route path="/gift-list" element={<GiftList />} />
                <Route path="/add-gift" element={<AddGift />} /> */}
                <Route path="/tasks" element={<Tasks listId={drive}/>} />
                <Route path="/add-list" element={<AddList/>} />
                <Route path="/add-task" element={<AddTask/>} />
                <Route path="/" element={<Navigate to="/home" />} />
            </Routes>
        </div>
    );
}

export default Routing;
