import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import ListModel from "../../../Models/ListModel";
import listsService from "../../../Services/list-service";
import Tasks from "../../TasksArea/tasks/tasks";
import "./Home.css";


function Home(): JSX.Element {


  const [lists, setLists] = useState<ListModel[]>([]);

  const navigate = useNavigate();

  useEffect(() => {
    listsService
      .getAllLists()
      .then(lists => setLists(lists))
      .catch(err => alert(err.message));
  }, []);

  const navigateTotasks = () => {

    navigate('/tasks');
  };

  const navigateToAddList = () => {

    navigate('/add-list');
  };

  return (
    <div className="Home">

      <h1>The List</h1>

      {lists.map(l =>
        <div key={l._id}>
          <br />

          <button onClick={navigateTotasks}>{l.listName}</button>

        </div>
      )}

      <br />
      <button onClick={navigateToAddList}>add list</button>
      {/* <button onClick={navigateToAddList}>add list</button> */}

      <Routes>
                <Route path="/home" element={<Home />} />
                {/* <Route path="/gift-list" element={<GiftList />} />
                <Route path="/add-gift" element={<AddGift />} /> */}
                <Route path="/tasks" element={<Tasks/>} />
                <Route path="/add-list" element={<AddList/>} />
                <Route path="/add-task" element={<AddTask/>} />
                <Route path="/" element={<Navigate to="/home" />} />
            </Routes>


    </div>
  );
}

export default Home;
