import { useForm } from "react-hook-form";
import { useNavigate, useParams } from "react-router-dom";
import TaskModel from "../../../Models/TaskModel";
import taskService from "../../../Services/task-service";
import "./edit.css";
import Button from '@mui/material/Button';
import IconButton from '@mui/material/IconButton';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import TextField from '@mui/material/TextField';

function Edit(): JSX.Element {

    const { taskId } = useParams()
    const { register, handleSubmit, formState } = useForm<TaskModel>();
    const navigate = useNavigate();

    async function submit(task: TaskModel) {
        try {
            const editedTask = await taskService.editTask(task,taskId )
            alert("task editted " + editedTask.description);
            navigate(-1)

        }
        catch (err: any) {
            alert(err.message);
        }
    }

    return (
        <div className="edit">
        <p>Edit</p>
            <p>{taskId}</p>

            <form className="form" onSubmit={handleSubmit(submit)} noValidate>

                <label>Edit task: </label>
                <input type="text" {...register("description", {
                    required: { value: true, message: "Missing task" }
                })} />
                <br/>
                <span>{formState.errors?.description?.message}</span>
                <br/>
                <br/>
                <button>Edit</button>

            </form>
        </div>
    );
}

export default Edit;
