    //         if (tasks[i].done === true) {
    //             count++
    //         }
    //     }
    //     let percent = count / tasks.length
    //     setPerc(percent)

    //     console.log(percent)
    // }, []);

    useEffect(() => {
        tasksService
            .getTasksByList(id)
            .then(tasks => setTasks(tasks))
            .catch(err => alert(err.message));
        console.log(tasks)

    }, []);

    useEffect(() => {

        let count = 0
        for (let i = 0; i < tasks.length; i++) {
            if (tasks[i].done === true) {
                count++
            }
        }
        let percent = (count / tasks.length)
        let x = percent.toFixed(1)
        console.log("X : " + x)
        setPerc(x)

        console.log(percent)
    }, [tasks]);

    return (
        <div className="PerDone">

            <p>{perc}</p>

            <p className="Done">done</p>

        </div>
    );
}

export default PerDone;
