import { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { useNavigate, useParams } from "react-router-dom";
import TaskModel from "../../../Models/TaskModel";
import { RootState } from "../../../Redux/Store";
import listsService from "../../../Services/list-service";
import tasksService from "../../../Services/task-service";
import "./tasks.css";


function Tasks(): JSX.Element {

    const tasks = useSelector<RootState, TaskModel[]>(state => state.tasksState.tasks)
    const { listId } = useParams()
    const { listName } = useParams()
    const navigate = useNavigate();

    const [checked, setChecked] = useState([]);

    // const handleChange = () => {
    //   setChecked(!checked);
    //   console.log(checked)
    // };

    // Add/Remove checked item from list
    const handleCheck = (event) => {
        var updatedList = [...checked];
        if (event.target.checked) {
            updatedList = [...checked, event.target.value];
        } else {
            updatedList.splice(checked.indexOf(event.target.value), 1);
        }
        setChecked(updatedList);
    };

    // Generate string of checked items
    const checkedItems = checked.length
        ? checked.reduce((total, item) => {
            return total + ", " + item;
        })
        : "";

    // Return classes based on whether item is checked
    var isChecked = (item) =>
        checked.includes(item) ? "checked-item" : "not-checked-item";



    useEffect(() => {
        tasksService
            .getTasksByList(listId)
            .catch(err => alert(err.message));
    }, []);





    const navigateTolists = () => {
        navigate('/home');
    };

    const navigateToAddTask = () => {
        navigate(`/add-task/${listId}`);
    };

    const navigateToEditTask = (id: string) => {
        console.log("taskid")
        console.log(id)
        navigate(`/edit-task/${id}`);
    };

    const deleteTask = (id: string) => {
        console.log(id)
        tasksService.deleteTasks(id)
        console.log("id1")
    }

    const deleteList = () => {
        const confirmDelete = window.confirm("Are you sure?");
        if (!confirmDelete) return;
        listsService.deleteList(listId)
        navigate('/home')
    }




    return (
        <div className="tasks">
            <button onClick={deleteList}>delete</button>
            <p>{listName}</p>

            <button onClick={navigateTolists}>back</button>
            {tasks.map(t =>
                <div key={t._id}>
                    <br />
                    <br />
                    {/* <input type="checkbox" checked={checked} onChange={handleChange} /> */}
                    <input value={t._id} type="checkbox" onChange={handleCheck} />
                    <span className={isChecked(t._id)}>{t._id}</span>
                    <p>{t.description}</p>
                    <button onClick={() => navigateToEditTask(t._id)}>edit</button>
                    <button onClick={() => deleteTask(t._id)}>del</button>

                    <div>
                        {`Items checked are: ${checkedItems}`}
                    </div>


                </div>
            )}
            <button onClick={navigateToAddTask}>add task</button>
        </div>
    );
}

export default Tasks;
