import { useForm } from "react-hook-form";
import { useNavigate, useParams } from "react-router-dom";
import TaskModel from "../../../Models/TaskModel";
import taskService from "../../../Services/task-service";
import "./addTask.css";
import Button from '@mui/material/Button';
import IconButton from '@mui/material/IconButton';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import TextField from '@mui/material/TextField';

function AddTask(): JSX.Element {

    const { listId } = useParams()
    const { listName } = useParams()
    const { register, handleSubmit, formState } = useForm<TaskModel>();
    const navigate = useNavigate();

    async function submit(task: TaskModel) {
        try {
            console.log("task")
            console.log(task)
            const addedTask = await taskService.addTask(task,listId )
            alert("task added " + addedTask.description);
            navigate(`/tasks/${listId}/${listName}`)

        }
        catch (err: any) {
            alert(err.message);
        }
    }

    return (
        <div className="addTask">
         <IconButton className="Back" onClick={()=>navigate(-1)}><ArrowBackIcon /></IconButton>


            <form onSubmit={handleSubmit(submit)} noValidate>

                <input type="text" {...register("description", {
                    required: { value: true, message: "Missing task" }
                })} />
                <span>{formState.errors?.description?.message}</span>

                <button>Add</button>

            </form>


        </div>
    );
}

export default AddTask;
