import { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { useNavigate, useParams } from "react-router-dom";
import TaskModel from "../../../Models/TaskModel";
import { RootState } from "../../../Redux/Store";
import tasksService from "../../../Services/task-service";
import "./tasks.css";


function Tasks(): JSX.Element {

    const tasks = useSelector<RootState,>(state => state.tasksState.tasks)

    const { listId } = useParams()
    const navigate = useNavigate();

    useEffect(() => {
        tasksService
            .getAllTasks()
            .catch(err => alert(err.message));
    }, []);

    const navigateTolists = () => {
        navigate('/home');
    };

    const navigateToAddTask = () => {

        navigate('/add-task');
    };

    const deleteTask = (id: string) => {
        console.log(id)
        tasksService.deleteTasks(id)
        console.log("id1")

    }


    return (
        <div className="tasks">
            <button>delete</button>
            <p>Task name</p>

            <button onClick={navigateTolists}>back</button>
            {tasks.map(t =>
                <div key={t._id}>
                    <br />
                    <br />
                    <input type="checkbox" value="done" />
                    <p>{t.description}</p>
                    <button>edit</button>
                    <button onClick={() => deleteTask(t._id)}>del</button>
                </div>
            )}
            <button onClick={navigateToAddTask}>add task</button>
        </div>
    );
}

export default Tasks;
