import { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { useLocation, useNavigate, useParams } from "react-router-dom";
import TaskModel from "../../../Models/TaskModel";
import { RootState } from "../../../Redux/Store";
import listsService from "../../../Services/list-service";
import taskService from "../../../Services/task-service";
import tasksService from "../../../Services/task-service";
import "./tasks.css";

import Button from '@mui/material/Button';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
import IconButton from '@mui/material/IconButton';
import Checkbox from '@mui/material/Checkbox';
import AddCircleIcon from '@mui/icons-material/AddCircle';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';

function Tasks(): JSX.Element {

    const tasks = useSelector<RootState, TaskModel[]>(state => state.tasksState.tasks)
    const { listId, listName } = useParams()
    const navigate = useNavigate();
    const location = useLocation()
    const [checked, setChecked] = useState(false);

    const [perc, setPerc] = useState(Number)
location.



    useEffect(() => {
        tasksService
            .getTasksByList(listId)
            .catch(err => alert(err.message));

    }, []);

    useEffect(() => {
        let count = 0
        for (let i = 0; i < tasks.length; i++) {
            if (tasks[i].done === true) {
                count++
            }
        }
        let percent = count / tasks.length
        setPerc(percent)

        console.log(percent)
    }, [checked]);

    const handleCheckbox = async (t: TaskModel) => {
        console.log(`1` + checked)
        setChecked(!checked);
        console.log(`2` + !checked)

        try {
            t.done = !checked
            await taskService.editTask(t, t._id)

        }
        catch (err: any) {
            alert(err.message);
        }

    };



    const navigateTolists = () => {
        navigate('/home');
    };

    const navigateToAddTask = () => {
        navigate(`/add-task/${listId}`);
    };

    const navigateToEditTask = (id: string) => {
        console.log("taskid")
        console.log(id)
        navigate(`/edit-task/${id}`);
    };

    const deleteTask = (id: string) => {
        console.log(id)
        tasksService.deleteTasks(id)
        console.log("id1")
    }

    const deleteList = () => {
        const confirmDelete = window.confirm("Are you sure?");
        if (!confirmDelete) return;
        listsService.deleteList(listId)
        navigate('/home')
    }




    return (
        <div className="Tasks">

            <IconButton className="Back" onClick={navigateTolists}><ArrowBackIcon /></IconButton>

            <IconButton className="DelList" onClick={deleteList}><DeleteIcon fontSize="small" /></IconButton>

            <br />
            <br />

            <h3 color="info">{listName}</h3>

            {tasks.map(t =>
                <div className="TaskLine" key={t._id}>
                    <br />
                    <input type="checkbox" className="Checkbox" checked={checked} onChange={() => handleCheckbox(t)} />
                    <p color="info">{t.description}</p>
                    <IconButton color="info" onClick={() => navigateToEditTask(t._id)}><EditIcon fontSize="small" /></IconButton>
                    <IconButton color="info" onClick={() => deleteTask(t._id)}> <DeleteIcon fontSize="small" /></IconButton>
                </div>
            )}

            <div className="Add">
                <IconButton
                    color="secondary"
                    onClick={navigateToAddTask}><AddCircleIcon /></IconButton>
            </div>
        </div>
    );
}

export default Tasks;
