import { useForm } from "react-hook-form";
import { useNavigate, useParams } from "react-router-dom";
import TaskModel from "../../../Models/TaskModel";
import taskService from "../../../Services/task-service";
import "./edit.css";

function Edit(): JSX.Element {

    const { taskId } = useParams()
    const { register, handleSubmit, formState } = useForm<TaskModel>();
    const navigate = useNavigate();

    async function submit(task: TaskModel) {
        try {
            console.log("task")
            console.log(task)
            const editTask = await taskService.addTask(task,listId )
            alert("task added " + addedTask.description);
            navigate(`/tasks/${listId}`)

        }
        catch (err: any) {
            alert(err.message);
        }
    }

    return (
        <div className="edit">
            <p>Edit</p>
            <p>{taskId}</p>

            <form onSubmit={handleSubmit(submit)} noValidate>


                <label>Edit task: </label>
                <input type="text" {...register("description", {
                    required: { value: true, message: "Missing task" }
                })} />
                <span>{formState.errors?.description?.message}</span>

                <button>Add</button>

            </form>
        </div>
    );
}

export default Edit;
