    //         const response = await axios.get<ProductModel[]>(config.productsUrl + "delayed");
    //         const products = response.data;
    //         store.dispatch(fetchProductsAction(products));
    //     }
    //     return store.getState().productsState.products;
    // }

    // Delete task: 
    public async deleteTasks(taskId: string): Promise<void> {
      await axios.delete(config.deletetasksUrl + taskId);  
      store.dispatch(deleteTaskAction(taskId));
    }
 
