    const { listId } = useParams()
    const { register, handleSubmit, formState } = useForm<TaskModel>();
    const navigate = useNavigate();

    return (
        <div className="addTask">
            <p>add task</p>
            <p>{listId}</p>

            <form onSubmit={handleSubmit(submit)} noValidate>


                <label>Write task: </label>
                <input type="text" {...register("listName", {
                    required: { value: true, message: "Missing lift name" }
                })} />
                <span>{formState.errors?.listName?.message}</span>

                <button>Add</button>

            </form>


        </div>
    );
}

export default AddTask;
