import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import ListModel from "../../../Models/ListModel";
import listsService from "../../../Services/list-service";
import Tasks from "../../TasksArea/tasks/tasks";
import "./Home.css";


function Home({sendDataToParent:string}): JSX.Element {


  const [lists, setLists] = useState<ListModel[]>([]);

  const navigate = useNavigate();

  useEffect(() => {
    listsService
      .getAllLists()
      .then(lists => setLists(lists))
      .catch(err => alert(err.message));
  }, []);

  const navigateTotasks = (id:string) => {
    console.log(id)
    sendDataToParent(id);
    // listId(id)
    navigate('/tasks');
  };

  const navigateToAddList = () => {
    navigate('/add-list');
  };

  return (
    <div className="Home">

      <h1>The List</h1>

      {lists.map(l =>
        <div key={l._id}>
          <br />

          <button onClick={()=>navigateTotasks(l._id)}>{l.listName}</button>
          {/* <button onClick={navigateTotasks}>{l.listName}</button> */}

        </div>
      )}

      <br />

      <button onClick={navigateToAddList}>add list</button>





    </div>
  );
}

export default Home;
