import { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { useNavigate, useParams } from "react-router-dom";
import TaskModel from "../../../Models/TaskModel";
import { RootState } from "../../../Redux/Store";
import listsService from "../../../Services/list-service";
import taskService from "../../../Services/task-service";
import tasksService from "../../../Services/task-service";
import "./tasks.css";

import Button from '@mui/material/Button';


function Tasks(): JSX.Element {

    const tasks = useSelector<RootState, TaskModel[]>(state => state.tasksState.tasks)
    const { listId } = useParams()
    const { listName } = useParams()
    const navigate = useNavigate();

    const [checked, setChecked] = useState(false);

    const [perc, setPerc] = useState(Number)




    useEffect(() => {
        tasksService
            .getTasksByList(listId)
            .catch(err => alert(err.message));
    }, []);

    useEffect(() => {
        let count = 0
        for (let i = 0; i < tasks.length; i++) {
            if (tasks[i].done === true) {
                count++
            }
        }
        let percent = count / tasks.length
        setPerc(percent)

        console.log(percent)
    }, [checked]);

    const handleCheckbox = async (t: TaskModel) => {
        console.log(`1` + checked)
        setChecked(!checked);
        console.log(`2` + !checked)

        try {
            t.done = !checked
            await taskService.editTask(t, t._id)

        }
        catch (err: any) {
            alert(err.message);
        }

    };



    const navigateTolists = () => {
        navigate('/home');
    };

    const navigateToAddTask = () => {
        navigate(`/add-task/${listId}`);
    };

    const navigateToEditTask = (id: string) => {
        console.log("taskid")
        console.log(id)
        navigate(`/edit-task/${id}`);
    };

    const deleteTask = (id: string) => {
        console.log(id)
        tasksService.deleteTasks(id)
        console.log("id1")
    }

    const deleteList = () => {
        const confirmDelete = window.confirm("Are you sure?");
        if (!confirmDelete) return;
        listsService.deleteList(listId)
        navigate('/home')
    }




    return (
        <div className="tasks">
            <button onClick={deleteList}>delete</button>
            <p>{listName}</p>

            <button onClick={navigateTolists}>back</button>
            {tasks.map(t =>
                <div key={t._id}>
                    <br />                   
                    <input type="checkbox" checked={checked} onChange={() => handleCheckbox(t)} />
                    <p>{t.description}</p>
                    <button onClick={() => navigateToEditTask(t._id)}>edit</button>
                    <button onClick={() => deleteTask(t._id)}>del</button>
                </div>
            )}
            <Button
                variant="contained"
                   color="secondary"
                sx={{
                    width: 63,
                    height: 63,
                    borderRadius: 30,
                }}
                onClick={navigateToAddTask}>+</Button>
        </div>
    );
}

export default Tasks;
