  const size = 63

  const navigateTotasks = (l: ListModel) => {
    console.log(l)
    // listId(id)
    navigate(`/tasks/${l._id}/${l.listName}`);
  };

  const navigateToAddList = () => {
    navigate('/add-list');
  };



  return (
    <div className="Home">

      <h1>The List</h1>

      {lists.map(l =>
        <div className="listName" key={l._id} >
          <div className="percent">
          <PerDone  id={l._id} />
          </div>
          <Button variant="contained" onClick={() => navigateTotasks(l)}>{l.listName}</Button>
          {/* <button onClick={() => navigateTotasks(l._id)}>{l.listName}</button> */}

