import { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import ListModel from "../../../Models/ListModel";
import listsService from "../../../Services/list-service";
import Tasks from "../../TasksArea/tasks/tasks";
import "./Home.css";


// function Home(): JSX.Element {
function Home(): JSX.Element {


  const [lists, setLists] = useState<ListModel[]>([]);

  const navigate = useNavigate();

  // const location = useLocation()
  useEffect(() => {
    listsService
      .getAllLists()
      .then(lists => setLists(lists))
      .catch(err => alert(err.message));
  }, []);



  const navigateTotasks = (l: ListModel) => {
    console.log(l)
    // listId(id)
    navigate(`/tasks/${l._id}/${l.listName}`);
  };

  const navigateToAddList = () => {
    navigate('/add-list');
  };

  return (
    <div className="Home">

      <h1>The List</h1>

      {lists.map(l =>
        <div key={l._id}>


          <br />

          <button onClick={() => navigateTotasks(l)}>{l.listName}</button>
          {/* <button onClick={() => navigateTotasks(l._id)}>{l.listName}</button> */}

        </div>
      )}

      <br />

      <button onClick={navigateToAddList}>add list</button>





    </div>
  );
}

export default Home;
